import React, { useState } from 'react';
import { Form, Button, Card } from 'react-bootstrap';
import { useNavigate } from 'react-router-dom';
import 'bootstrap-icons/font/bootstrap-icons.css';
import axios from 'axios';
import { toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

const DirForm = () => {
  const [defDirectory, setDefDirectory] = useState('');
  const [lefDirectory, setLefDirectory] = useState('');
  const [libDirectory, setLibDirectory] = useState('');
  const [techDirectory, setTechDirectory] = useState('');
  const [validationResults, setValidationResults] = useState([]);
  const [files, setFiles] = useState([]);
  const [selectedFile, setSelectedFile] = useState('');

  const navigate = useNavigate();
  const handleSubmit = () => {
    if (
      validationResults.every((result) => result.isValid) &&
      defDirectory &&
      lefDirectory &&
      libDirectory &&
      techDirectory
    ) {
      navigate('/InputVariables');
    } else {
      console.log('Please make sure all paths are valid before proceeding');
    }
  };

  const handleValidation = async () => {
    const response = await fetch('http://localhost:5000/validate-directories', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({
        defDirectory,
        lefDirectory,
        libDirectory,
        techDirectory,
      }),
    });

    const results = await response.json();
    setValidationResults(results);
    console.log(results);

    if (results.every((result) => result.isValid)) {
      toast.success('All paths are valid!');
    } else {
      toast.error('Some paths are invalid. Please check and try again.');
    }
  };
//save paths
  const handleSave = async () => {
    // Get the file paths from the state variables
    const data = {
      defDirectory,
      lefDirectory,
      libDirectory,
      techDirectory
    };
  
    // Send a POST request to the /save-path endpoint with the file paths in the request body
    try {
      const response = await axios.post('http://localhost:5000/save-path', data);
      // If the save operation was successful, show a success message
      toast.success("path saved")
      console.log('Design saved successfully');
    } catch (err) {
      // If there was an error, show an error message
      toast.error("error saving the path")
      console.error('Error saving design:', err);
    }
  };
  
  const handleListFiles = async (directoryPath) => {
    try {
      const response = await axios.post('http://localhost:5000/list-files', { directoryPath });
      setFiles(response.data.files);
    } catch (err) {
      console.error('Error listing files:', err);
    }
  };

  const handleSelectFile = async (directoryPath) => {
    try {
      await axios.post('http://localhost:5000/select-file', { directoryPath, selectedFile });
      console.log('File selected successfully');
    } catch (err) {
      console.error('Error selecting file:', err);
    }
  };

  return (
    <Card className="col-7 mt-3" style={{ margin: 'auto' }}>
      <Card.Header>Design Directory inputs</Card.Header>
      <Card.Body>
        <Form>
          <Form.Group>
            <Form.Label>DEF File Directory:</Form.Label>
            <div className="input-group">
              <Form.Control
                type="text"
                value={defDirectory}
                onChange={(e) => setDefDirectory(e.target.value)}
              />
              <div className="input-group-append">
                <span className="input-group-text">
                  {validationResults.find((result) => result.name === 'DEF')?.isValid ? (
                    <i className="bi bi-check-circle text-success"></i>
                  ) : (
                    <i className="bi bi-x-circle text-danger"></i>
                  )}
                </span>
              </div>
            </div>
          </Form.Group>
          <Form.Group>
            <Form.Label>LEF File Directory:</Form.Label>
            <div className="input-group">
              <Form.Control
                type="text"
                value={lefDirectory}
                onChange={(e) => setLefDirectory(e.target.value)}
              />
              <div className="input-group-append">
                <span className="input-group-text">
                  {validationResults.find((result) => result.name === 'LEF')?.isValid ? (
                    <i className="bi bi-check-circle text-success"></i>
                  ) : (
                    <i className="bi bi-x-circle text-danger"></i>
                  )}
                </span>
              </div>
            </div>
          </Form.Group>
          <Form.Group>
            <Form.Label>LIB File Directory:</Form.Label>
            <div className="input-group">
              <Form.Control
                type="text"
                value={libDirectory}
                onChange={(e) => setLibDirectory(e.target.value)}
              />
              <div className="input-group-append">
                <span className="input-group-text">
                  {validationResults.find((result) => result.name === 'LIB')?.isValid ? (
                    <i className="bi bi-check-circle text-success"></i>
                  ) : (
                    <i className="bi bi-x-circle text-danger"></i>
                  )}
                </span>
              </div>
            </div>
          </Form.Group>
          <Form.Group>
            <Form.Label>Tech File Directory:</Form.Label>
            <div className="input-group">
              <Form.Control
                type="text"
                value={techDirectory}
                onChange={(e) => setTechDirectory(e.target.value)}
              />
              <div className="input-group-append">
                <span className="input-group-text">
                  {validationResults.find((result) => result.name === 'Tech')?.isValid ? (
                    <i className="bi bi-check-circle text-success"></i>
                  ) : (
                    <i className="bi bi-x-circle text-danger"></i>
                  )}
                </span>
              </div>
            </div>
          </Form.Group>

          {/* Add new form group for listing and selecting files */}
          <Form.Group>
            <Form.Label>List Files:</Form.Label>
            <div className="input-group">
              {/* Add a dropdown to select the directory to list files from */}
              <select onChange={(e) => handleListFiles(e.target.value)}>
                <option value="">Select a directory</option>
                {defDirectory && (
                  <option value={defDirectory}>DEF Directory ({defDirectory})</option>
                )}
                {lefDirectory && (
                  <option value={lefDirectory}>LEF Directory ({lefDirectory})</option>
                )}
                {libDirectory && (
                  <option value={libDirectory}>LIB Directory ({libDirectory})</option>
                )}
                {techDirectory && (
                  <option value={techDirectory}>Tech Directory ({techDirectory})</option>
                )}
              </select>

              {/* Add a dropdown to select a file from the list of files */}
              {files.length > 0 && (
                <>
                  <Form.Label>Select File:</Form.Label>
                  <select
                    value={selectedFile}
                    onChange={(e) => setSelectedFile(e.target.value)}
                  >
                    <option value="">Select a file</option>
                    {files.map((file) => (
                      <option key={file} value={file}>
                        {file}
                      </option>
                    ))}
                  </select>

                  {/* Add a button to select the chosen file */}
                  {selectedFile && (
                    <>
                      {/* Add a button to select the chosen file */}
                      {/* Pass the directory path of the selected file to the handleSelectFile function */}
                      {defDirectory.includes(selectedFile) && (
                        <Button onClick={() => handleSelectFile(defDirectory)}>
                          Select File
                        </Button>
                      )}
                      {lefDirectory.includes(selectedFile) && (
                        <Button onClick={() => handleSelectFile(lefDirectory)}>
                          Select File
                        </Button>
                      )}
                      {libDirectory.includes(selectedFile) && (
                        <Button onClick={() => handleSelectFile(libDirectory)}>
                          Select File
                        </Button>
                      )}
                      {techDirectory.includes(selectedFile) && (
                        <Button onClick={() => handleSelectFile(techDirectory)}>
                          Select File
                        </Button>
                      )}
                    </>
                  )}
                </>
              )}
            </div>
          </Form.Group>

          {/* Update the existing buttons to use the new functions */}
          <div className="d-flex justify-content-end">
            {/* Update the "Validate Directories" button to use the new handleValidation function */}
            <Button
              className="mt-3 me-2"
              style={{ backgroundColor: '#4D135A', border: 'none' }}
              onClick={handleValidation}
            >
              Validate Directories
            </Button>

            {/* Update the "Proceed" button to use the new handleSubmit function */}
            {/* Disable the button if not all directories are valid or if no file is selected */}
            <Button
              className="mt-3"
              style={{ backgroundColor: '#08411D', border: 'none' }}
              onClick={handleSubmit}
              disabled={
                !(
                  validationResults.every((result) => result.isValid) &&
                  defDirectory &&
                  lefDirectory &&
                  libDirectory &&
                  techDirectory &&
                  selectedFile
                )
              }
            >
              Proceed
            </Button>

            {/* Update the "Save" button to use the new handleSave function */}
            <Button
              className="mt-3"
              style={{ backgroundColor: '#08411D', border: 'none' }}
              onClick={handleSave}
              disabled={
                !(
                  validationResults.every((result) => result.isValid) &&
                  defDirectory &&
                  lefDirectory &&
                  libDirectory &&
                  techDirectory &&
                  selectedFile
                )
              }
            >
              Save
            </Button>
          </div>
        </Form>
      </Card.Body>
    </Card>
  );
};

export default DirForm;
