import React, { useState } from "react";
import { Card, Form, Button, Table } from "react-bootstrap";
import CardHeader from "react-bootstrap/esm/CardHeader";
import axios from "axios";
import { toast } from "react-toastify";

const InputVariables = (props) => {
  const [design, setDesign] = useState("");
  const [numCpu, setNumCpu] = useState("");
  const [powerOpt, setPowerOpt] = useState("");
  const [genEff, setGenEff] = useState("");
  const [submittedData, setSubmittedData] = useState(props.location.state?.formData);

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post(
        "http://localhost:5000/save-design-variable",
        {
          design: design,
          num_cpu: numCpu,
          power_opt: powerOpt,
          gen_eff: genEff,
        }
      );
      toast.success("variables added successfully!");
      console.log(response.data.message);

      setSubmittedData({
        design,
        numCpu,
        powerOpt,
        genEff
      });
    } catch (err) {
      toast.error("error adding variables");
      console.error(err.message);
    }
  };

  const handleNumCpuChange = (e) => {
    const value = e.target.value;
    // Check if the value is a valid number
    if (/^\d*$/.test(value)) {
      // Parse the value as a number
      const numValue = parseInt(value, 10);
      // Check if the value is between 8 and 64
      if (value === "" || (numValue >= 8 && numValue <= 64)) {
        setNumCpu(value);
      }
    }
  };

  return (
    <Card className="col-7 mt-3" style={{ margin: "auto" }}>
      <CardHeader>
        <Card.Title>Design Variables</Card.Title>
      </CardHeader>
      <Card.Body>
        <Form onSubmit={handleSubmit}>
          {/* ... existing form code ... */}
        </Form>
      </Card.Body>
      {submittedData && (
        <div className="mt-3">
          <h5>Submitted Data:</h5>
          <Table striped bordered hover>
            <thead>
              <tr>
                <th>Field</th>
                <th>Value</th>
              </tr>
            </thead>
            <tbody>
              <tr>
                <td>Design</td>
                <td>{submittedData.design}</td>
              </tr>
              <tr>
                <td>Number of CPUs</td>
                <td>{submittedData.numCpu}</td>
              </tr>
              <tr>
                <td>Power Optimization</td>
                <td>{submittedData.powerOpt}</td>
              </tr>
              <tr>
                <td>Generation Efficiency</td>
                <td>{submittedData.genEff}</td>
              </tr>
            </tbody>
          </Table>
        </div>
      )}
    </Card>
  );
};

export default InputVariables;
